package com.foodshare.chat.repository;import java.util.List;import java.util.Optional;import org.springframework.data.mongodb.repository.MongoRepository;import org.springframework.stereotype.Repository;import com.foodshare.domain.UserRoomVisibility;@Repositorypublic interface UserRoomVisibilityRepository extends MongoRepository<UserRoomVisibility, String> {	List<UserRoomVisibility> findByUserId(String userId);	Optional<UserRoomVisibility> findByUserIdAndChatRoomId(String userId, String chatRoomId);}