package com.foodshare.domain;import java.util.Date;import org.springframework.data.mongodb.core.mapping.Document;import jakarta.persistence.Id;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;@Data@NoArgsConstructor@AllArgsConstructor@Builder@Document(collection = "chatMessages")public class ChatMessage {	@Id	private String id;	private String chatRoomId;	private String sender;	private String content;	private Date timestamp;}