package com.foodshare.domain;import org.springframework.data.annotation.TypeAlias;import org.springframework.data.mongodb.core.mapping.Document;import org.springframework.data.mongodb.core.mapping.Field;import jakarta.persistence.Id;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;@Data@NoArgsConstructor@AllArgsConstructor@Builder@Document(collection = "userRoomVisibility")@TypeAlias("userRoomVisibility")public class UserRoomVisibility {	@Id	private String id;	private String userId;	private String chatRoomId;	private String lastVisibleMessageId;	@Field("isHidden")	private boolean isHidden;}