package com.foodshare.notification.sse.component;import org.springframework.web.servlet.mvc.method.annotation.SseEmitter;public class EmitterWrapper {	private final SseEmitter emitter;	private volatile boolean completed;	public EmitterWrapper(SseEmitter emitter) {		this.emitter = emitter;		this.completed = false;		this.emitter.onCompletion(() -> this.completed = true);		this.emitter.onTimeout(() -> this.completed = true);		this.emitter.onError((e) -> this.completed = true);	}	public SseEmitter getEmitter() {		return emitter;	}	public void completeEmitter() {		if (!completed) {			emitter.complete();			completed = true;		}	}	public boolean isCompleted() {		return completed;	}}